import React from "react";
import { SPRITE_SIZE } from "../../config/constants";

function getTileSprite(type) {
  switch (type) {
    case 0:
      return "grass";
    case 5:
      return "rock";
    case 6:
      return "tree";
  }
}

function MapTile(props) {
  return (
    <div
      className="tile"
      style={{
        height: SPRITE_SIZE,
        width: SPRITE_SIZE,
      }}
    >
      {props.tile}
    </div>
  );
}

function MapRow(props) {
  return (
    <div className="row">
      {props.tiles.map((tile) => (
        <MapTile tile={tile} />
      ))}
    </div>
  );
}

function Map(props) {
  return (
    <div
      style={{
        width: "800px",
        height: "400px",
        backgroundColor: "green",
        border: "4px solid white",
      }}
    >
      {props.tiles.map((row) => (
        <MapRow tiles={row} />
      ))}
    </div>
  );
}

export default Map;
